from day3 import Day3
import pytest

@pytest.fixture
def day3():
    return Day3()

def test_single_move(day3):
    hill = [
        [".", ".", ".", "."],
        [".", ".", ".", "#"],
    ]

    trees_hit = day3.count_trees_hit(hill)

    assert trees_hit == 1

def test_overwrap_move(day3):
    hill = [
        [".", ".", ".", "."],
        [".", ".", ".", "#"],
        [".", ".", "#", "."],
        [".", "#", ".", "."],
    ]

    trees_hit = day3.count_trees_hit(hill)

    assert trees_hit == 3

def test_day_3_sample(day3):
    hill = [
		[".", ".", "#", "#", ".", ".", ".", ".", ".", ".", "."],
		["#", ".", ".", ".", "#", ".", ".", ".", "#", ".", "."],
		[".", "#", ".", ".", ".", ".", "#", ".", ".", "#", "."],
		[".", ".", "#", ".", "#", ".", ".", ".", "#", ".", "#"],
		[".", "#", ".", ".", ".", "#", "#", ".", ".", "#", "."],
		[".", ".", "#", ".", "#", "#", ".", ".", ".", ".", "."],
		[".", "#", ".", "#", ".", "#", ".", ".", ".", ".", "#"],
		[".", "#", ".", ".", ".", ".", ".", ".", ".", ".", "#"],
		["#", ".", "#", "#", ".", ".", ".", "#", ".", ".", "."],
		["#", ".", ".", ".", "#", "#", ".", ".", ".", ".", "#"],
		[".", "#", ".", ".", "#", ".", ".", ".", "#", ".", "#"]
	]

    trees_hit = day3.count_trees_hit(hill)

    assert trees_hit == 7

